INSERT INTO chembl.kinase_uniprot (entry,entry_name,protein_names,gene_names,length,chembl_id,unigene,kegg,geneid,catalytic_activity,enzyme_regulation,target_sequence) VALUES ("P08581","MET_HUMAN","Hepatocyte growth factor receptor (HGF receptor) (EC 2.7.10.1) (HGF/SF receptor) (Proto-oncogene c-Met) (Scatter factor receptor) (SF receptor) (Tyrosine-protein kinase Met)","MET",1390,"CHEMBL3717","Hs.132966 ","hsa:4233 ","4233","CATALYTIC ACTIVITY: ATP + a [protein]-L-tyrosine = ADP + a [protein]-L-tyrosine phosphate. {ECO:0000255|PROSITE-ProRule:PRU10028}.","ENZYME REGULATION: In its inactive state, the C-terminal tail interacts with the catalytic domain and inhibits the kinase activity. Upon ligand binding, the C-terminal tail is displaced and becomes phosphorylated, thus increasing the kinase activity.","");
