INSERT INTO chembl.kinase_uniprot (entry,entry_name,protein_names,gene_names,length,chembl_id,unigene,kegg,geneid,catalytic_activity,enzyme_regulation,target_sequence) VALUES ("Q13043","STK4_HUMAN","Serine/threonine-protein kinase 4 (EC 2.7.11.1) (Mammalian STE20-like protein kinase 1) (MST-1) (STE20-like kinase MST1) (Serine/threonine-protein kinase Krs-2) [Cleaved into: Serine/threonine-protein kinase 4 37kDa subunit (MST1/N)  Serine/threonine-protein kinase 4 18kDa subunit (MST1/C)]","STK4 KRS2 MST1",487,"CHEMBL4598","Hs.472838 ","hsa:6789 ","6789","CATALYTIC ACTIVITY: ATP + a protein = ADP + a phosphoprotein.","ENZYME REGULATION: Inhibited by the C-terminal non-catalytic region. Activated by caspase-cleavage. Full activation also requires homodimerization and autophosphorylation of Thr-183. Activated by RASSF1 which acts by preventing its dephosphorylation. {ECO:0000269|PubMed:15109305, ECO:0000269|PubMed:21199877, ECO:0000269|PubMed:8702870}.","");
