use chembl;
drop table if EXISTS kinase_uniprot;
CREATE TABLE kinase_uniprot
(
    entry VARCHAR(10) primary key NOt NULL,
    entry_name VARCHAR(30) not null,
    protein_names varchar(1000) not null,	
    gene_names varchar(300) not null,
    length int null,
    chembl_id varchar(30) not null,
    unigene	varchar(300) null,
    kegg varchar(300) null,
    geneid	varchar(300) null,
    catalytic_activity varchar(1000) null,
    enzyme_regulation varchar(5000) null,	
    target_sequence varchar(8000) null

);

CREATE UNIQUE INDEX kinase_uniprot_entry_uindex ON kinase_uniprot (entry);

